mapper.select:
SELECT /* cpQuery.getCpList 2013.06.04 정대원 */
*
FROM COMPANY

    
        /* type이 null 이 아니면 검색 */
        
            
                company_name like '%${query}%'
            
            
                company_mgmtno = #{query}
            
            
                (
                company_name like '%${query}%'
                OR
                company_mgmtno = #{query}
                )
            
        
    
    AND del_yn = 'N'
    AND company_type = 'CP'

ORDER BY reg_dt DESC

    LIMIT #{ startRownum }, #{ perPage }

SELECT /* cpQuery.getCpCount 2013.06.04 정대원 */ count(*) FROM ( SELECT * FROM COMPANY
/* type이 null 이 아니면 검색 */
company_name like '%${query}%'
company_mgmtno = #{query}
( company_name like '%${query}%' OR company_mgmtno = #{query} )
AND del_yn = 'N' AND company_type = 'CP'
) tmp

mapper.update:
UPDATE COMPANY /* cpQuery.deleteCp "del_yn 컬럼을 'Y'로 변경한다." 2013.06.04 정대원 */
del_yn = 'Y', mod_dt = NOW()
WHERE company_mgmtno = #{ company_mgmtno }
