mapper.select:
SELECT
    *
FROM SERIES

    
    /* type이 null 이 아니면 검색 */
        
            
                series_name like '%${query}%'
            
            
                series_mgmtno = #{query}
            
            
                (
                series_name like '%${query}%'
                OR
                series_mgmtno = #{query}
                )
            
        
    
    
        AND cate_id = #{ cate_id }

SELECT count(*) FROM ( SELECT * FROM SERIES /* type이 null 이 아니면 검색 */
series_name like '%${query}%'
series_mgmtno = #{query}
series_name like '%${query}%' OR series_mgmtno = #{query}
) tmp

mapper.update:
UPDATE SERIES
series_name = #{ series_name }, cate_id = #{ cate_id }, mod_dt = NOW(),
WHERE series_mgmtno = #{ series_mgmtno }

mapper.delete:
DELETE FROM SERIES
WHERE series_mgmtno = #{ series_mgmtno }
LIMIT 1
