mapper.select:
SELECT /* userQuery.getLoginResult 정대원 */
COUNT(id)
FROM USER
WHERE ID = #{ id }
AND PWD = #{ pwd }

mapper.update:
UPDATE USER
pwd = #{ pwd }, name = #{ name }, team = #{ team }, title = #{ title }, phoneno = #{ phoneno }, email = #{ email }, level_cd = #{ level_cd }, mod_dt = NOW()
WHERE id = #{ id }
